    private void botonCargarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonCargarActionPerformed
    String nombre= txtNombre.getText().trim();
    String apellido= txtApellido.getText().trim();
    String nacionalidad=txtNacionalidad.getText().trim();
    String tarjetasTotales= txtTarjetasTotales.getText().trim();
    boolean valido= true; 
    //falta obtener la fecha, arreglar el problema con la libreria 
    
    //se eliminan errores anteriores 
    lblErrorNombre.setText("");
    lblErrorApellido.setText("");
    lblErrorNacionalidad.setText("");
    lblErrorTarjetasTotales.setText("");
    //Se ponen las casillas blancas
    txtApellido.setBackground(Color.WHITE);
    txtNombre.setBackground(Color.WHITE);
    txtNacionalidad.setBackground(Color.WHITE);
    txtTarjetasTotales.setBackground(Color.WHITE);
    
    //Validacion de casillas vacias y contenido correcto
    //Nombre
    if(nombre.isEmpty()){//si la casillas esta vacia
        lblErrorNombre.setText("El nombre no puede estar vac√≠o.");//aparece un label con mensaje
        txtNombre.setBackground(Color.pink);//y se marca la casilla en rojo
        valido=false;
    } else if(!nombre.matches("[a-zA-Z√°√©√≠√≥√∫√Å√â√ç√ì√ö√±√ë\\s]+")){//regex
        lblErrorNombre.setText("Nombre inv√°lido.Solo letras");
        txtNombre.setBackground(Color.pink);
        valido=false;
    }
    //Apellido
    if(apellido.isEmpty()){
        lblErrorApellido.setText("El nombre no puede estar vac√≠o.");
        txtNombre.setBackground(Color.pink);
        valido=false;
    } else if(!apellido.matches("[a-zA-Z√°√©√≠√≥√∫√Å√â√ç√ì√ö√±√ë\\s]+")){
        lblErrorApellido.setText("Apellido inv√°lido. Solo letras");
        txtApellido.setBackground(Color.PINK);        
        valido=false;
    }
    //Nacionalidad
    if(nacionalidad.isEmpty()){
        lblErrorNacionalidad.setText("El nombre no puede estar vac√≠o.");
        txtNacionalidad.setBackground(Color.pink);
        valido=false;
    } else if(!nacionalidad.matches("[a-zA-Z√°√©√≠√≥√∫√Å√â√ç√ì√ö√±√ë\\s]")){
        lblErrorNacionalidad.setText("El nombre no puede estar vac√≠o.");
        txtNacionalidad.setBackground(Color.pink);
    }
    //tarjetas Amarillas
    if(tarjetasTotales.isEmpty()){
        lblErrorTarjetasTotales.setText("El nombre no puede estar vac√≠o.");
        txtNombre.setBackground(Color.pink);
        valido=false;
    }          
     //validaci√≥n de fecha 
      
      
      //Controlers para tarjetas
      try{
          int TarjetasTotales= Integer.parseInt(tarjetasTotales);
          if(TarjetasTotales<0 || TarjetasTotales>1000){
              txtTarjetasTotales.setBackground(Color.PINK);
              valido= false;
          }
      }catch(NumberFormatException ex){
          txtTarjetasTotales.setBackground(Color.PINK);
          valido = false;
      }
      
      if(!valido){
          JOptionPane.showMessageDialog(this,"Corrija las casillas en rojo");
          return; 
      }
      
      //Si todo esta bien, cargar info a jugador 
      ControladorArbitro controller= new ControladorArbitro(listaArbitros); 
      controller.colocarNombre(nombre);
      controller.colocarApellido(apellido);
      controller.colocarNacionalidad(nacionalidad);
      controller.colocarTarjetasTotales(TarjetasTotales);// se esta pasando entero no integer, cambiar a tipo integer en modelo? 
      //insertar jugador a la lista
      controller.ColocarArbitroLista();
      JOptionPane.showMessageDialog(this, "Se cargo el jugador correctamente");
       
      //borrar texto
      txtApellido.setText("");
      txtNombre.setText("");
      txtNacionalidad.setText("");
      txtTarjetasTotales.setText("");
    }//GEN-LAST:event_botonCargarActionPerformed

